								SRV_String_setCopy(&getCoreDataRsp.pvsl[i].pvs[j].propertyId.idSpec, ppvsl[i].pvs[j].PropertyId.IdSpec, ov_string_getlength(ppvsl[i].pvs[j].PropertyId.IdSpec));
